function updateTime(){var currentTime=new Date,hours=currentTime.getHours(),minutes=currentTime.getMinutes(),seconds=currentTime.getSeconds(),day=currentTime.getDate(),month=currentTime.getMonth()+1,year=currentTime.getFullYear();if(day<10&&(day="0"+day),month<10&&(month="0"+month),hours<10&&(hours="0"+hours),minutes<10&&(minutes="0"+minutes),seconds<10&&(seconds="0"+seconds),hours<12){var t_str=hours+":"+minutes+":"+seconds+" a.m.";document.getElementById("time").innerHTML=t_str}if(hours>12)if(hourspm=hours-12,hourspm<10){hourspm="0"+hourspm;var t_str=hourspm+":"+minutes+":"+seconds+" p.m.";document.getElementById("time").innerHTML=t_str}else{var t_str=hourspm+":"+minutes+":"+seconds+" p.m.";document.getElementById("time").innerHTML=t_str}if(12==hours){var t_str=(hours=12)+":"+minutes+":"+seconds+" p.m.";document.getElementById("time").innerHTML=t_str}if(0==hours){var t_str=(hours=12)+":"+minutes+":"+seconds+" a.m.";document.getElementById("time").innerHTML=t_str}var d_str=day+"-"+month+"-"+year;document.getElementById("date").innerHTML=d_str}setInterval(updateTime,1e3);